import{a as z}from"./chunk-BVWRDFU3.js";import"./chunk-DFFVOSJF.js";import{Fb as n,Ja as c,Ua as t,lb as s,mb as i,nb as a,ob as o}from"./chunk-BP7CLSB6.js";var y=class r{faqItems=[{title:"Pedicure klasyczny",answer:"Jest to klasyczna piel\u0119gnacja st\xF3p. Rozpoczyna si\u0119 od z\u0142uszczenie sk\xF3rek, opi\u0142owania i wypolerowania p\u0142ytki paznokciowej, kt\xF3rej nadajemy po\u017C\u0105dany kszta\u0142t. Nast\u0119pnie oczyszczamy podeszw\u0119 stopy i wmasowujemy odpowiedni krem \u2013 w zale\u017Cno\u015Bci od potrzeb sk\xF3ry. Oczywi\u015Bcie p\u0142ytk\u0119 paznokciow\u0105 malujemy dowolnie wybranym kolorem lakieru zabezpieczaj\u0105c go bazami tak, aby d\u0142ugo cieszy\u0142y nas nienagannym wygl\u0105dem."},{title:"Pedicure leczniczy",answer:"Opr\xF3cz typowych dla pedicure zabieg\xF3w zostaje wykonany kompres zmi\u0119kczaj\u0105cy, kt\xF3ry u\u0142atwia usuni\u0119cie zrogowacia\u0142ego nask\xF3rka. Nast\u0119pnie dodatkowa okluzja g\u0142\u0119boko zmi\u0119kcza, nawil\u017Ca i regeneruje sk\xF3r\u0119 st\xF3p pozostawiaj\u0105c je z d\u0142ugotrwa\u0142ym uczuciem lekko\u015Bci."},{title:"Pedicure SPA",answer:"Rozpoczyna si\u0119 hydromasa\u017Cem k\u0105pieli st\xF3p w solach zmi\u0119kczaj\u0105cych co u\u0142atwia p\xF3\u017Aniejsze usuni\u0119cie nadmiaru zrogowacia\u0142ego nask\xF3rka. P\u0142ytce paznokciowej nadajemy po\u017C\u0105dany kszta\u0142t, z\u0142uszczamy sk\xF3rki, polerujemy i lakierujemy. Wykonujemy te\u017C gruboziarnisty piling ca\u0142ej stopy.<br/><br/>Nast\u0119pnie przy pomocy naturalnych preparat\xF3w(na bazie mi\u0119ty, kamfory i mocznika) wykonujemy odpr\u0119\u017Caj\u0105cy masa\u017C relaksacyjny. Pobudzi to kr\u0105\u017Cenie likwiduj\u0105c jednocze\u015Bnie uczucie ci\u0119\u017Cko\u015Bci i opuchni\u0119cia zapewniaj\u0105c d\u0142ugotrwa\u0142e uczucie \u015Bwie\u017Co\u015Bci i lekko\u015Bci."},{title:"Pedicure podologiczny",answer:"Obejmuje kompleksowe zabiegi w zakresie specjalistycznego pedicure:<br/><br/>\u2013 fachowe i bezbolesne usuwanie modzeli i odcisk\xF3w \u2013 profesjonalne zabiegi na p\u0119kaj\u0105cych pi\u0119tach i rozpadlinach sk\xF3rnych<br/>\u2013 profesjonalne zabiegi na stopach potliwych<br/>\u2013 opatrunki obci\u0105\u017Caj\u0105ce, koryguj\u0105ce, rekonstrukcje p\u0142ytki paznokciowej<br/>\u2013 dob\xF3r i sprzeda\u017C odpowiednich \u015Brodk\xF3w piel\u0119gnacyjnych<br/>\u2013 pomoc w doborze odpowiedniego obuwia<br/><br/><b>Oferujemy bezp\u0142atne konsultacje podologiczne.<b/>"},{title:"Klamra Frasera na wrastaj\u0105ce paznokcie",answer:"Natychmiastow\u0105 ulg\u0119 przynosz\u0105 indywidualne klamry ortonyksyjne typu Frasera. Klamry s\u0105 indywidualnie dopasowane d\u0142ugo\u015Bci\u0105 i kszta\u0142tem ramion do typu paznokcia. Zaktywowana pos\u0142uguje si\u0119 spr\u0119\u017Cynuj\u0105c\u0105 si\u0142\u0105 drutu, kt\xF3ry odci\u0105gaj\u0105c boki paznokcia bezbole\u015Bnie koryguje jego kszta\u0142t.<br/><br/>Cele ortonyksji:<br/><br/>\u2013 korekcja paznokcia wrastaj\u0105cego, wk\u0119caj\u0105cego<br/>\u2013 regeneracja chorobowo zmienianego \u0142o\u017Cyska<br/>\u2013 profilaktyka nawrot\xF3w po operacyjnym usuni\u0119ciu paznokcia<br/>\u2013 natychmiastowa ulga w stanach bolesno\u015Bci wywo\u0142anych wrastaniem<br/><br/>Wskazania do zastosowania klamry:<br/><br/>\u2013 wrastanie i deformacja p\u0142ytki<br/>\u2013 alternatywa po operacji chirurgicznych<br/>\u2013 zaburzenie wzrostu paznokcia<br/>\u2013 hiperkeratoza \u0142o\u017Cyska"},{title:"Klamra koryguj\u0105ca",answer:"Klamr\u0119 koryguj\u0105c\u0105 stosuje si\u0119 w przypadku wrastaj\u0105cych paznokci. Jest to cienki pasek ze stali chirurgicznej, kt\xF3ry przykleja si\u0119 na p\u0142ytk\u0119 paznokciow\u0105. Przez \u015Bcis\u0142e przy\u0142\u0105czenie klamry z paznokciem dzia\u0142a ona elastycznie odginaj\u0105c brzegi paznokcia ku g\xF3rze, dzi\u0119ki czemu zapobiega jego wrastaniu w tkank\u0119 mi\u0119kk\u0105. P\u0142aska forma nie powoduje ucisku w obuwiu, klamra jest wodoodporna, niewidoczna i mo\u017Cna j\u0105 przykrywa\u0107 lakierem. W zale\u017Cno\u015Bci od wielko\u015Bci paznokcia stosuje si\u0119 klamry r\xF3\u017Cnej wielko\u015Bci. Przed jej za\u0142o\u017Ceniem nale\u017Cy wykona\u0107 pedicure, ale bez k\u0105pieli wodnej, u\u017Cywaj\u0105c jedynie p\u0142ynu zmi\u0119kczaj\u0105cego sk\xF3r\u0119. U os\xF3b m\u0142odych cz\u0119sto wystarcza jednorazowy zabieg, u starszych klamry zak\u0142ada si\u0119 do ca\u0142kowitego wyleczenia \u2013 aby paznokie\u0107 odzyska\u0142 w\u0142a\u015Bciwy kszta\u0142t."},{title:"Malowanie paznokci st\xF3p",answer:"<b>Klasyczne</b><br/>Obejmuje z\u0142uszczenie nask\xF3rka, opi\u0142owanie i wypolerowanie p\u0142ytki paznokciowej, kt\xF3rej nadajemy po\u017C\u0105dany kszta\u0142t. P\u0142ytk\u0119 paznokciow\u0105 malujemy dowolnie wybranym kolorem lakieru zabezpieczaj\u0105c go bazami tak, aby paznokcie d\u0142ugo cieszy\u0142y nas nienagannym wygl\u0105dem.<br/><br/><b>Malowanie paznokci st\xF3p Shellac/hybryda</b><br/><br/>Shellac to po\u0142\u0105czenie najlepszych cech klasycznych lakier\xF3w do paznokci \u2013 czyli \u0142atwo\u015Bci w nak\u0142adaniu i \u015Bci\u0105ganiu, braku konieczno\u015Bci pi\u0142owania p\u0142ytki paznokciowej, a tak\u017Ce szybko\u015Bci wykonania manicure oraz \u017Celu \u2013 czyli wytrzyma\u0142o\u015Bci i nie\u015Bcieralno\u015Bci. Dzi\u0119ki temu ponad 14 dni mo\u017Cna cieszy\u0107 si\u0119 idealnie trwa\u0142ym kolorem bez wzgl\u0119du na to, jak intensywnie u\u017Cywamy naszych paznokci.<br/>Shellac to produkt hipoalergiczny \u2013 nie powoduje \u017Cadnej szkody dla naturalnej p\u0142ytki paznokcia.<br/>Koniec z odpryskiwaniem, smugami i d\u0142ugim czasem wysychania lakieru."},{title:"Stylizacja i rekonstrukcja paznokci",answer:"Obejmuje wszystkie dost\u0119pne metody (akrylowa, \u017Celowa) przed\u0142u\u017Cania i rekonstrukcji p\u0142ytki paznokciowej (np. w przypadku bardzo ma\u0142ej lub zniekszta\u0142conej, uszkodzonej powierzchni)."},{title:"K\u0105piel parafinowa",answer:"Oddaj si\u0119 chwili przyjemno\u015Bci oraz zatroszcz si\u0119 o swoje stopy. Aromatyczny peeling st\xF3p z przemasowaniem z\u0142uszczy i wyg\u0142adzi nask\xF3rek. Nast\u0119pnie na\u0142o\u017Cenie mocno od\u017Cywczej maski i wykonanie k\u0105pieli parafinowej silnie wyg\u0142adzi, zregeneruje i nat\u0142u\u015Bci sk\xF3r\u0119 st\xF3p, a przy tym wspaniale odpr\u0119\u017Ca."}];static \u0275fac=function(e){return new(e||r)};static \u0275cmp=t({type:r,selectors:[["app-podiatry"]],decls:11,vars:1,consts:[[1,"sale-page"],[1,"sale-header"],[1,"divider"],[1,"sale-content"],[1,"sale-text"],["src","assets/oferta-podstrony/podologia.jpg","alt","Podologia",1,"sale-img"],[1,"w-100",3,"items"]],template:function(e,k){e&1&&(i(0,"section",0)(1,"header",1)(2,"h1"),n(3,"Podologia"),a(),o(4,"div",2),a(),i(5,"div",3)(6,"div",4)(7,"p"),n(8," Warto piel\u0119gnowa\u0107 stopy w odpowiedni spos\xF3b, by d\u0142ugo zachowa\u0107 ich zdrowie i estetyczny wygl\u0105d. W naszym gabinecie pos\u0142ugujemy si\u0119 specjalistycznymi frezarkami, kt\xF3re bezpiecznie, szybko i na d\u0142ugo pozb\u0119d\u0105 si\u0119 zgrubie\u0144 na stopach, przywracaj\u0105c im g\u0142adko\u015B\u0107 i delikatno\u015B\u0107. "),a()()(),o(9,"img",5)(10,"app-faq",6),a()),e&2&&(c(10),s("items",k.faqItems))},dependencies:[z],encapsulation:2})};export{y as Podiatry};
