import{a as c}from"./chunk-BVWRDFU3.js";import"./chunk-DFFVOSJF.js";import{Fb as o,Ja as s,Ua as r,lb as z,mb as a,nb as e,ob as n}from"./chunk-BP7CLSB6.js";var w=class t{faqItems=[{title:"Laseroterapia",answer:"E-LIGHT \u2013 to nowoczesna technika medycyny estetycznej bazuj\u0105ca na tzw. impulsywnym \u017Ar\xF3dle \u015Bwiat\u0142a. Dzia\u0142anie urz\u0105dze\u0144 wykorzystywanych w tej technice jest podobne do dzia\u0142ania lasera, jednak w przeciwie\u0144stwie do lasera (kt\xF3rego \u015Bwiat\u0142o emituje sp\xF3jn\u0105, skoncentrowan\u0105 wi\u0105zk\u0119 fali o jednej d\u0142ugo\u015Bci fali) emituje wi\u0105zki o szerokim spektrum niesp\xF3jnych fal \u015Bwietlnych r\xF3\u017Cnej d\u0142ugo\u015Bci. Poszczeg\xF3lne wi\u0105zki docieraj\u0105 do r\xF3\u017Cnych partii sk\xF3ry i w zale\u017Cno\u015Bci od potrzeby mog\u0105 uzyskiwa\u0107 po\u017C\u0105dane zmiany w tkankach. Poprzez zastosowanie odpowiednich filtr\xF3w pasmowych istnieje mo\u017Cliwo\u015B\u0107 dotarcia energii \u015Bwietlnej, kt\xF3ra na odpowiednim poziomie (g\u0142\u0119boko\u015Bci) zamienia si\u0119 w energi\u0119 ciepln\u0105 i skutecznie leczy schorzenie, osi\u0105gaj\u0105c zamierzony efekt. W ten spos\xF3b do tkanek mo\u017Ce dotrze\u0107 wy\u0142\u0105cznie promieniowanie odpowiednie dla danej patologii. Obszary zdrowej sk\xF3ry nie ulegaj\u0105 uszkodzeniu, wi\u0105zka \u015Bwiat\u0142a nie wnika w g\u0142\u0105b organizmu, a jej dzia\u0142anie jest wy\u0142\u0105cznie miejscowe. Zabiegi IPL nie maj\u0105 skutk\xF3w ubocznych i nie wymagaj\u0105 rezygnacji z normalnego trybu \u017Cycia po wykonanym zabiegu. Pr\xF3by kliniczne i kilkuletnie do\u015Bwiadczenia dowodz\u0105 i\u017C s\u0105 to metody zabiegowe bezpieczne i w por\xF3wnaniu z tradycyjnymi metodami o wiele skuteczniejsze. Powszechnie najlepsz\u0105 uznana metod\u0105 na ca\u0142ym \u015Bwiecie jest technologia IPL laserowa epilacja TBL jest najnowsz\u0105 inwencj\u0105, specjalnie skonstruowan\u0105 aby dawa\u0107 najbezpieczniejsze, najszybsze, najtrwalsze rezultaty w epuacji, leczeniu tr\u0105dziku naczyniowego i przebarwie\u0144.<br/><br/>W efekcie ultrad\u017Awi\u0119ki sondami wykorzystuj\u0105 wibracje d\u017Awi\u0119kowe w celu redukcji zmarszczek czyni\u0105c sk\xF3r\u0119 mocniejsz\u0105 i g\u0142adsz\u0105, stymuluj\u0105c mi\u0119\u015Bnie modeluj\u0105c sylwetk\u0119.<br/><br/>Ultrad\u017Awi\u0119ki przyspieszaj\u0105 kataliz\u0119 i metabolizm kom\xF3rkowy sk\xF3ry, zamieni\u0107 PH na zasadowe, z\u0142agodzi\u0107 zapalenie, zakwasy oraz b\xF3l. Poprawiaj\u0105 oddychanie b\u0142ony kom\xF3rkowej, szybko uwalniaj\u0105 substancje od\u017Cywcze z kosmetyk\xF3w (soneforeza). Idealnie dope\u0142niaj\u0105 i wzmacniaj\u0105 szereg zabieg\xF3w daj\u0105c spektakularne efekty regeneracji i odnowy sk\xF3ry."}];static \u0275fac=function(i){return new(i||t)};static \u0275cmp=r({type:t,selectors:[["app-laser-therapy"]],decls:11,vars:1,consts:[[1,"sale-page"],[1,"sale-header"],[1,"divider"],[1,"sale-content"],[1,"sale-text"],["src","assets/oferta-podstrony/karboksyterapia.jpg","alt","Laseroterapia",1,"sale-img"],[1,"w-100",3,"items"]],template:function(i,l){i&1&&(a(0,"section",0)(1,"header",1)(2,"h1"),o(3,"Laseroterapia"),e(),n(4,"div",2),e(),a(5,"div",3)(6,"div",4)(7,"p"),o(8," Laseroterapia E-LIGHT i ultrad\u017Awi\u0119ki to nowoczesne, nieinwazyjne metody odm\u0142adzania i leczenia sk\xF3ry. Zabiegi skutecznie redukuj\u0105 zmarszczki, tr\u0105dzik, przebarwienia i modeluj\u0105 sylwetk\u0119, zapewniaj\u0105c widoczne efekty bez okresu rekonwalescencji. "),e()()(),n(9,"img",5)(10,"app-faq",6),e()),i&2&&(s(10),z("items",l.faqItems))},dependencies:[c],encapsulation:2})};export{w as LaserTherapy};
